//App.js 
import React, { useEffect, useState } from 'react'; 
import './App.css'; 

function BMICalculator(){
	const [heightValue, setHeightValue] = useState("");
	const [weightValue, setWeightValue] = useState("");
	const [BMIValue, setBMIValue] = useState("");
	const [BMIMessage, setBMIMessage] = useState("");

	if(heightValue&& weightValue)
	{
		let message="";
		let bmi =0;
		let height= heightValue/100 ;
		setHeightValue(height);
		bmi = weightValue/(heightValue)^2
		setBMIValue(bmi)
		if (bmi<18.5)
			setBMIMessage("you are underweight");

		else if (bmi>18.5 && bmi<25)
		{
			setBMIMessage("you are normal weight");
		}
		else if (bmi>25 && bmi<29.9)
		{
			setBMIMessage("you are overweight");
		}
		else
		{
			setBMIMessage("you are obese");
		}
	}



	return(
		<div>
			<h1> BMI Calculator</h1>
			<label htmlFor='height'>Please enter your height</label>
			<input type='number' value={heightValue} id='height' onChange={(e)=>setHeightValue(e.target.value)} ></input>
			<label htmlFor='weight'>Please enter your weight</label>
			<input type='number' value={weightValue} id='weight' onChange={(e)=>setWeightValue(e.target.value)} ></input>
			<buttton onClick={BMICalculator}>Calculate BMI</buttton>
			{BMIValue&& BMIMessage}
			{
				<div className="result"> 
				<p> 
					Your BMI: <span className="bmi-value">{BMIValue}</span> 
				</p> 
				<p> 
					Result: <span className="bmi-message">{BMIMessage}</span> 
				</p> 
			</div> 
			}
		</div>
	)
	;
}

export default BMICalculator;
